import './App.css';
import React from 'react';

class App extends React.Component {
  constructor () {
    super()
    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      email: '', 
      estado: false,
    }
  }


  handleChange() {

  }

  render () {
    return (
      <div>
        <h1> Formulário em React: Componentes Controlados</h1>
        <form>
          <label> Name:
            <input type='name'  onChange={this.handleChange}></input>
          </label>
          <label> Email:
            <input type='email' onChange={this.handleChange} ></input>
          </label>
          <label> Informações complementares:
            <textarea></textarea>
          </label>
          <label>Estado:
          <select onChange={this.handleChange}> 
            <option></option>
            <option>SC</option>
            <option>RS</option>
            <option>RJ</option>
          </select>
          </label>
        </form>
      </div>
    )
  }

}

export default App;
